INSERT INTO `%DB_PREFIX%faq` VALUES ('1', '咨询客服', '如何提现', '（1）关注微信服务号“方维互动直播”\r\n（2）同方维互动直播账户绑定\r\n（3）通过微信公众号“方维互动直播”申请提现\r\n（4）审核通过马上到账', '9', '58', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('2', '直播问题', '怎么打开美颜？', '进入直播间后打开房间设置功能，打开美颜即可', '10', '27', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('3', '充值问题', '充值有上限吗？', '方维互动直播充值是没有限制的，您充值时提示有限制，您可以查询一下您的银行卡或者是其他您的支付方式是否有限额。', '0', '70', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('6', '直播问题', '怎么成为热门？', '热门是系统随机推荐的，您多直播、多和粉丝互动您上热门的几率会高一点，您也可以把您的直播分享到多个平台让您的朋友都来观看您的精彩直播。', '1', '51', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('7', '账号问题', '怎么认证？', '打开方维互动直播—个人主页—方维互动直播认证—填写相关信息—提交即可。', '32', '115', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('8', '账号问题', '为什么总是显示账户信息过期，请重新登录？', '亲爱的方维互动直播用户，如果收到“账号信息已过期”的弹窗提示，请到方维互动直播—个人主页—设置—退出登录，并重新登录账号即可。如还不能解决，请您联系在线客服并详细描述您的问题。', '3', '17', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('11', '直播问题', '怎么使用后置摄像头？', '进入直播间—点击屏幕右下角有“...”-点击“翻转”', '11', '10', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('12', '咨询客服', '咨询客服', '客服QQ：', '12', '41', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('14', '充值问题', '怎么充值钻石？', '打开方维互动直播应用，你可以在个人主页—我的钻石里进行充值或者在观看他人直播时点击礼物—充值。您可以选择不同的档位进行充值。', '17', '11', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('15', '账号问题', '怎么设置管理员？', '在您自己的直播间点击粉丝头像，在弹出的页面点左上角管理—设置管理员，就可以把您喜欢的人设置成管理员了。', '18', '5', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('16', '账号问题', '账号与安全怎么绑定手机号', '个人主页—设置—账号与安全-手机绑定绑定手机号，验证码是短信形式发送，绑定手机号后可以使用手机号登录。', '19', '7', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('17', '账号问题', '个人主页是什么？', '点击最下方右边的按钮，就进入到个人主页中心界面，在这里可以查看到您个人信息的信息，还有收益、等级等信息。', '20', '5', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('18', '账号问题', '如何更改个人信息？', '点击个人主页左上角的铅笔图标，进入到个人信息编辑界面，在这里可以更改您的昵称、头像、性别、个性签名。', '21', '1', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('19', '账号问题', '如何选择热门地区？', '在首页点击热门，就可以选择您喜欢的地区，观看该地区热门主播的精彩直播。', '22', '1', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('20', '账号问题', '账户可以注销吗？', '方维互动直播是第三方快捷登录的，所以账号是不可以注销的。每一种登录方式都是一个全新的方维互动直播号，您想要其他账号您可以用其他方式登录哦。', '23', '3', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('21', '账号问题', '登录失败怎么办？', '登录失败建议您关闭app、切换网络试一下，重新登录即可。', '24', '1', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('22', '直播问题', '为什么直播时脸是反的', '方维互动直播是在手机上进行的。使用前置摄像头直播的时候就相当于在照镜子一样，所以说才会看到感觉脸是反的。', '25', '4', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('23', '直播问题', '提示相机不可用', '安卓用户在手机设置里找到权限管理，找到方维互动直播打开所有相机权限就可以了；ios用户在审核设置—隐私—相机—打开方维互动直播相机权限就可以了。', '26', '4', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('24', '直播问题', '怎么隐藏公屏和弹幕？', '进入直播间向右滑屏就可以隐藏公屏和弹幕，即可无遮挡的观看您喜欢的主播了哟。', '27', '4', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('25', '直播问题', '直播没有人看怎么办？', '多直播，让自己的直播诙谐有趣，方维互动直播这么好玩，可以拉朋友一起嗨；多和粉丝互动，培养自己的粉丝群；您也可以和其他主播小伙伴多多交流共享经验。超级大主播指日可待！', '28', '5', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('26', '直播问题', '公屏怎么不显示评论', '建议您切换网络、关闭app重新打开试试，网络不稳定的时候会显示延迟。', '29', '12', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('27', '直播问题', '直播时怎么分享到其他平台？', '在开始直播前，填写直播标题页面点亮相应的平台图标，就可以分享到其他平台邀请朋友观看直播。在直播中是没有办法分享自己的直播给朋友们的哦。', '30', '21', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('28', '直播问题', '为什么我直播好友收不到通知？', '安卓用户确认好友的方维互动直播应用是否关闭好友推送，进入个人主页—设置—推送管理—直播消息提醒；ios用户确认好友是否关闭方维互动直播的推送，还需在系统设置里允许通知：设置—通知—选择方维互动直播允许通知就可以了。', '31', '23', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('29', '直播问题', '收到骚扰，怎么拉黑？', '点击进入ta的主页，右下方点击拉黑即可。', '32', '54', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('30', '直播问题', '怎么关注好友，取消关注？', '进入ta个人主页，页面下方可以选择关注好友，再次点击已关注就可以取消关注了。', '0', '2', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('31', '直播问题', '怎么播放音乐？', '在直播页面下方点击音乐图标，进入可以搜索歌曲，在搜索框中搜索您想要演唱的歌曲，点击“下载”图标，即可演唱。', '33', '71', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('32', '充值问题', 'ios充值方式？', 'ios版本app充值只支持AppStore充值。', '0', '9', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('33', '充值问题', '怎么支付宝和微信充值？', '目前只有安卓版本支持微信和支付宝充值的，在个人主页—我的钻石里选择您喜欢的支付方式即可。', '0', '10', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('34', '充值问题', '充错账户，点错充值档位怎么办？', '购买钻石输入密码前，请仔细确认金额后再购买。错充等无法退还，还请您务必小心操作哦。', '0', '6', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('35', '充值问题', '微信充值提示充值超限怎么办？', '方维互动直播没有限制充值金额，银行卡是有限额的，查看绑定银行卡的限额，建议您提高一下您银行卡的支付限额。', '0', '7', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('36', '充值问题', '充值成功钻石没有到,怎么办？', '充值未到账请您点击设置-帮助与反馈-咨询客服，找到我们在线客服的并提供以下资料，方便我们核实及处理。\r\n1.方维互动直播号\r\n2.充值日期、充值时间是商务、下午或是晚上，大概是几点\r\n3.方维互动直播版本号是多少（在个人主页设置内查看）\r\n4.提交您交易账单的截图（苹果app store充值在绑定苹果ID的邮箱里查看账单）\r\n5.请您详细说明具体一共几笔没有到账个，未到账金额各是多少。', '0', '7', '1', '1');
INSERT INTO `%DB_PREFIX%faq` VALUES ('37', '充值问题', '充值的入口有哪些？', '直播间没有礼物列表可以充值，个人中心处的充值也可以充值', '34', '15', '1', '1');
