CREATE TABLE `%DB_PREFIX%user` (
  `id` int(11) NOT NULL COMMENT 'ID',
  `nick_name` varchar(100) NOT NULL COMMENT '用户昵称',
  `user_pwd` varchar(255) NOT NULL COMMENT '用户密码',
  `signature` varchar(255) NOT NULL COMMENT '个性签名',
  `create_time` int(11) NOT NULL COMMENT '创建时间',
  `update_time` int(11) NOT NULL COMMENT '更新时间',
  `is_authentication` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否认证 0指未认证  1指待审核 2指认证 3指审核不通过',
  `login_type` tinyint(1) NOT NULL COMMENT '0：微信；1：QQ；2：手机；3：微博',
  `is_effect` tinyint(1) NOT NULL COMMENT '有效性标识',
  `money` decimal(20,2) NOT NULL COMMENT '金额',
  `login_ip` varchar(50) NOT NULL COMMENT '登陆IP',
  `province` varchar(20) NOT NULL COMMENT '省',
  `city` varchar(20) NOT NULL COMMENT '市',
  `is_edit_sex` tinyint(1) NOT NULL DEFAULT '1' COMMENT '能否修改性别 1为可修改 0为不可修改',
  `sex` tinyint(1) NOT NULL DEFAULT '0' COMMENT '性别 0:未知, 1-男，2-女',
  `birthday` int(11) NOT NULL COMMENT '出生日期',
  `is_remind` tinyint(1) NOT NULL DEFAULT '1' COMMENT '接受推送消息 0-不接收，1-接收',
  `focus_count` int(11) NOT NULL DEFAULT '0' COMMENT '关注的人数',
  `intro` text NOT NULL COMMENT '个人简介',
  `code` varchar(255) NOT NULL COMMENT '编码',
  `sina_id` varchar(255) NOT NULL COMMENT '新浪ID',
  `sina_token` varchar(255) NOT NULL COMMENT '新浪令牌',
  `sina_secret` varchar(255) NOT NULL COMMENT '新浪密码',
  `sina_url` varchar(255) NOT NULL COMMENT '新浪跳转地址',
  `tencent_id` varchar(255) NOT NULL COMMENT '腾讯ID',
  `tencent_token` varchar(255) NOT NULL COMMENT '腾讯令牌',
  `tencent_secret` varchar(255) NOT NULL COMMENT '腾讯密码',
  `tencent_url` varchar(255) NOT NULL COMMENT '腾讯跳转地址',
  `verify` varchar(255) NOT NULL COMMENT '验证',
  `user_level` int(11) NOT NULL DEFAULT '1' COMMENT '用户等级;%DB_PREFIX%user_level.level',
  `mobile` varchar(20) NOT NULL COMMENT '手机号',
  `user_type` int(11) NOT NULL DEFAULT '0' COMMENT '用户类型 0指 普通用户 ，1指企业会员',
  `is_has_send_success` tinyint(1) NOT NULL COMMENT '是否发送成功',
  `verify_setting_time` int(11) NOT NULL COMMENT '设置时间',
  `authentication_type` varchar(255) NOT NULL COMMENT '认证类型',
  `authentication_name` varchar(255) NOT NULL COMMENT '认证名称',
  `contact` varchar(255) NOT NULL COMMENT '联系方式',
  `from_platform` varchar(255) NOT NULL COMMENT '来自平台',
  `wiki` varchar(255) NOT NULL COMMENT '百度百科',
  `identify_hold_image` varchar(255) NOT NULL COMMENT '手持身份证照片',
  `identify_positive_image` varchar(255) NOT NULL COMMENT '身份证正面',
  `identify_nagative_image` varchar(255) NOT NULL COMMENT '身份证反面',
  `wx_openid` varchar(255) NOT NULL COMMENT '微信openid',
  `gz_openid` varchar(255) NOT NULL COMMENT '公众号的微信openid',
  `qq_openid` varchar(255) NOT NULL COMMENT 'QQopenid',
  `investor_send_info` varchar(255) NOT NULL COMMENT '审核信息',
  `paypassword` varchar(255) NOT NULL COMMENT '提现和支付密码',
  `source_url` varchar(255) NOT NULL COMMENT '来源url',
  `pid` int(11) NOT NULL COMMENT '推荐人id',
  `score` int(11) NOT NULL COMMENT '积分',
  `point` int(11) NOT NULL COMMENT '信用值',
  `emotional_state` varchar(20) NOT NULL DEFAULT '保密' COMMENT '情感状态',
  `job` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT '主播' COMMENT '职位',
  `head_image` varchar(255) NOT NULL COMMENT '用户头像',
  `thumb_head_image` varchar(255) NOT NULL COMMENT '头像缩略图',
  `qq_id` varchar(255) NOT NULL COMMENT 'QQID',
  `qq_token` varchar(255) NOT NULL COMMENT 'QQ令牌',
  `v_type` tinyint(2) NOT NULL DEFAULT '0' COMMENT '认证类型:0: 未认证;1:普通认证;2:企业认证;3:支付宝认证',
  `v_explain` varchar(255) DEFAULT NULL COMMENT '加v认证说明',
  `v_icon` varchar(255) DEFAULT NULL COMMENT '认证图标',
  `fans_count` int(11) NOT NULL DEFAULT '0' COMMENT '粉丝数',
  `ticket` decimal(13,2) NOT NULL DEFAULT '0.00' COMMENT '印票数',
  `refund_ticket` decimal(13,2) NOT NULL COMMENT '已提现的印票',
  `diamonds` int(11) NOT NULL DEFAULT '0' COMMENT '当前还剩钻石数',
  `use_diamonds` int(10) NOT NULL DEFAULT '0' COMMENT '累计消费的钻石数',
  `usersig` varchar(2000) NOT NULL COMMENT '用户签名',
  `expiry_after` int(10) NOT NULL COMMENT '用户签名过期时间',
  `is_online` tinyint(1) NOT NULL DEFAULT '0' COMMENT '1:用户在线;0:不在线;通过服务端监听更新',
  `wx_unionid` varchar(255) NOT NULL COMMENT '微信unionid',
  `user_name` varchar(255) NOT NULL COMMENT '微信昵称',
  `synchronize` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否同步成功 : 0指同步失败 ， 1指同步成功',
  `login_time` datetime NOT NULL COMMENT '最近上线时间',
  `logout_time` datetime NOT NULL COMMENT '最近下线时间',
  `is_agree` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否同意直播协议 0 表示不同意 1表示同意',
  `online_time` int(11) NOT NULL COMMENT '用户总的观看时间，单位为秒',
  `subscribe` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否关注公众号 0未关注 1已关注',
  `is_robot` tinyint(1) NOT NULL DEFAULT '0' COMMENT '1:机器人',
  `apns_code` varchar(64) NOT NULL COMMENT '友盟消息推送服务对设备的唯一标识。Android的device_token是44位字符串, iOS的device-token是64位。',
  `device_type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '1:android; 2:ios',
  `video_count` int(10) NOT NULL DEFAULT '0' COMMENT '可回看的直播数量',
  `is_ban` tinyint(1) NOT NULL DEFAULT '0' COMMENT '禁播状态 0-正常；1-禁播',
  `ban_time` int(11) NOT NULL COMMENT '禁播结束时间',
  `identify_number` varchar(32) NOT NULL COMMENT '身份证号码',
  `authent_list_id` int(10) NOT NULL DEFAULT '0' COMMENT '%DB_PREFIX%authent_list.id 认证ID',
  `family_id` int(11) NOT NULL COMMENT '家族ID',
  `family_chieftain` tinyint(1) NOT NULL COMMENT '是否家族长；0：否、1：是',
  `is_admin` tinyint(1) DEFAULT '0' COMMENT '是否系统管理员 1-是 ；0-否',
  `last_login_level` int(11) DEFAULT NULL COMMENT '上一次登录时的用户等级',
  `binding_alipay` tinyint(1) NOT NULL DEFAULT '0',
  `alipay_name` varchar(255) NOT NULL,
  `alipay_account` varchar(255) NOT NULL,
  `room_title` varchar(100) DEFAULT NULL COMMENT '直播间名称',
  `luck_num` int(10) NOT NULL COMMENT '靓号号码',
  `alipay_user_id` varchar(100) NOT NULL COMMENT '支付宝认证的支付宝用户号',
  `alipay_authent_token` varchar(100) NOT NULL COMMENT '支付宝认证 token',
  `p_user_id` int(11) NOT NULL COMMENT '分销功能 上级用户ID',
  `ban_type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '禁播类型  0 禁止单账号直播, 1 禁止同IP直播，2 禁止同设备直播',
  `society_id` int(11) NOT NULL COMMENT '公会id',
  `society_chieftain` tinyint(1) NOT NULL COMMENT '是否公会长；0：否、1：是',
  `society_settlement_type` tinyint(1) NOT NULL COMMENT '结算方式；0：对私结算、1：对公结算',
  `is_vip` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否VIP会员；0：否、1：是',
  `vip_expire_time` int(11) NOT NULL DEFAULT '0' COMMENT '会员到期时间',
  `coin` int(11) NOT NULL DEFAULT '0' COMMENT '游戏币',
  `is_nospeaking` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否全局永久禁言 1-是，0-否',
  `society_ticket` int(10) NOT NULL COMMENT '公会主播上缴的印票',
  `view_count` int(10) NOT NULL DEFAULT '0' COMMENT '累计观看次数;累计观看次数明显大于其它用户观看次数时，及有可能是盗连接用户，需要禁用它',
  `is_hot_on` tinyint(1) NOT NULL DEFAULT '0' COMMENT '禁热门 0-正常；1-禁止',
  PRIMARY KEY (`id`),
  KEY `idx_u_001` (`is_effect`) USING BTREE,
  KEY `idx_u_002` (`is_robot`) USING BTREE,
  KEY `idx_ecs_user_cc_1` (`wx_unionid`,`login_type`) USING BTREE,
  KEY `idx_ecs_user_cc_2` (`mobile`) USING BTREE,
  KEY `idx_ecs_user_cc_3` (`wx_openid`,`login_type`) USING BTREE,
  KEY `idx_ecs_user_cc_4` (`qq_openid`) USING BTREE,
  KEY `idx_ecs_user_cc_5` (`mobile`,`login_type`) USING BTREE,
  KEY `idx_ecs_user_cc_6` (`nick_name`,`is_robot`) USING BTREE,
  KEY `idx_user_sina_id` (`sina_id`) USING BTREE,
  KEY `idx_user_society_id` (`society_id`) USING BTREE,
  KEY `idx_ecs_user_cc_9` (`id`,`device_type`,`is_effect`,`is_remind`) USING BTREE,
  KEY `idx_u_003` (`view_count`) USING BTREE,
  KEY `idx_ecs_deal_msg_cc_1` (`nick_name`,`is_robot`) USING BTREE,
  KEY `limit_cc_1` (`id`,`luck_num`,`nick_name`,`is_robot`) USING BTREE,
  KEY `limit_cc_2` (`id`,`is_robot`) USING BTREE,
  KEY `limit_cc_3` (`luck_num`,`is_robot`) USING BTREE,
  KEY `limit_cc_4` (`nick_name`,`is_robot`) USING BTREE,
  KEY `ind_%DB_PREFIX%user` (`is_robot`,`nick_name`) USING BTREE,
  KEY `ind_%DB_PREFIX%user_sina` (`sina_id`) USING BTREE,
  KEY `ht_1` (`is_effect`,`is_robot`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='//用户信息';

-- ----------------------------
-- Records of %DB_PREFIX%user
