-- ----------------------------
-- Table structure for `%DB_PREFIX%user_log`
-- ----------------------------
DROP TABLE IF EXISTS `%DB_PREFIX%user_log`;
CREATE TABLE `%DB_PREFIX%user_log` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `log_info` text NOT NULL COMMENT '日志信息',
  `log_time` int(11) NOT NULL COMMENT '日志时间',
  `log_admin_id` int(11) NOT NULL COMMENT '日志管理员ID',
  `money` double(20,4) NOT NULL COMMENT '金额',
  `user_id` int(11) NOT NULL COMMENT '用户ID',
  `type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '//类型 0表示充值 1表示提现 2赠送道具 3 兑换印票  4 分享获得印票 5 登录赠送积分 6 观看付费直播',
  `prop_id` int(11) NOT NULL COMMENT '道具ID号',
  `score` int(11) NOT NULL COMMENT '积分',
  `point` int(11) NOT NULL COMMENT '信用值',
  `podcast_id` int(11) NOT NULL COMMENT '主播ID',
  `diamonds` int(11) NOT NULL COMMENT '钻石数',
  `ticket` int(11) DEFAULT '0' COMMENT '票数',
  `video_id` int(11) DEFAULT NULL COMMENT '直播间ID',
  PRIMARY KEY (`id`),
  KEY `index_user_log` (`user_id`,`type`,`log_time`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='//帐户资金变动日志';

