-- Records of %DB_PREFIX%user_notice
-- ----------------------------

-- ----------------------------
-- Table structure for `%DB_PREFIX%user_refund`
-- ----------------------------
DROP TABLE IF EXISTS `%DB_PREFIX%user_refund`;
CREATE TABLE `%DB_PREFIX%user_refund` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `ticket` int(20) NOT NULL COMMENT '订单',
  `money` double(20,4) NOT NULL COMMENT '金额',
  `user_id` int(11) NOT NULL COMMENT '用户ID',
  `create_time` int(11) NOT NULL COMMENT '提现申请时间',
  `reply` text NOT NULL COMMENT '提现审核回复',
  `is_pay` tinyint(1) NOT NULL COMMENT '0 表示未审核;1 表示 允许操作成功；2 表示 未允许操作成功;3 表示 提现确认成功 4.表示支付失败',
  `pay_time` int(11) NOT NULL COMMENT '付款时间',
  `memo` text NOT NULL COMMENT '提现的备注',
  `pay_log` text NOT NULL COMMENT '支付说明',
  `user_bank_id` int(11) NOT NULL COMMENT '银行ID',
  `ybdrawflowid` varchar(255) DEFAULT NULL COMMENT '流水号',
  `partner_trade_no` varchar(255) NOT NULL COMMENT '商户订单号',
  `withdrawals_type` tinyint(1) NOT NULL COMMENT '提现方式：0:微信，1:支付宝,2:公会提现',
  PRIMARY KEY (`id`),
  KEY `user_id` (`user_id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='//用户提现记录';

